import React, { useState } from 'react';
import './PinPageStyle.css';

function PinPage() {
  const [pin, setPin] = useState('');
  const [error, setError] = useState('');

  const handlePinChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const input = e.target.value.replace(/\D/g, '').slice(0, 4);
    setPin(input);
  };

  const handleJoinGame = async () => {
    setError('');
    if (pin.length !== 4) {
      setError('Please enter a 4-digit pin.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pin }),
      });

      if (!response.ok) {
        const { error } = await response.json();
        setError(error || 'Invalid pin. Please try again.');
      } else {
        alert('Joined lobby successfully!');
      }
    } catch (err) {
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="pin-page">
      <h1>Enter Lobby Pin</h1>
      <input
        type="text"
        value={pin}
        onChange={handlePinChange}
        placeholder="Enter 4-digit pin"
        maxLength={4}
        className="pin-input"
      />
      <button onClick={handleJoinGame} className="join-button">
        Join Game
      </button>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default PinPage;
