import React, { useState } from 'react';
import './PinPageStyle.css';

function PinPage() {
  const [userId, setUserId] = useState('');
  const [pin, setPin] = useState('');
  const [showError, setShowError] = useState(false);

  const handleJoinGame = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/lobbies/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          pin,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Lobby code is valid. Proceeding to join game...');
        setShowError(false);
        // Add logic for redirecting to the lobby or updating UI
      } else {
        console.error(data.error);
        setShowError(true);
      }
    } catch (error) {
      console.error('Error joining the game:', error);
      setShowError(true);
    }
  };

  return (
    <div className="pin-page">
      <h1>Enter Lobby Code</h1>
      <div>
        <input
          type="text"
          placeholder="User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <input
          type="text"
          placeholder="PIN"
          maxLength={4}
          value={pin}
          onChange={(e) => setPin(e.target.value)}
        />
        <button onClick={handleJoinGame}>Join Game</button>
        {showError && <p className="error-message">Invalid Lobby PIN or Error Joining</p>}
      </div>
    </div>
  );
}

export default PinPage;
