import React, { useState } from 'react';
import './PinPageStyle.css';

function PinPage() {
  const [lobbyCode, setLobbyCode] = useState('');
  const [showError, setShowError] = useState(false);

  const handleLobbyCodeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const input = e.target.value;
    const numericInput = input.replace(/\D/g, '').slice(0, 4); // Limit to 4 digits
    setLobbyCode(numericInput);
  };

  const handleJoinGame = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/lobbies/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          lobbyId: 1, // Use the actual lobbyId or pass it dynamically
          userId: 'playerUser', // Replace with the actual user ID or prompt for it
          pin: lobbyCode,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Lobby code is valid. Proceeding to join game...');
        setShowError(false); // Hide error if PIN is correct
        // Redirect to the game or update state to show the game UI
      } else {
        console.error(data.error);
        setShowError(true); // Show error if PIN is incorrect
      }
    } catch (error) {
      console.error('Error joining the game:', error);
      setShowError(true);
    }
  };

  const handleCloseError = () => {
    setShowError(false);
  };

  return (
    <>
      <div className="spacer"></div>
      <div className="pin-page">
        <header className="header">
          <h1>Enter Lobby Code</h1>
          <div className="lobby-code">
            <input
              type="text"
              value={lobbyCode}
              onChange={handleLobbyCodeChange}
              placeholder="XXXX"
              maxLength={4}
            />
          </div>
        </header>
        <button className="start-game-button" onClick={handleJoinGame}>
          Join Game
        </button>
      </div>
      <div className="spacer2"></div>

      {showError && (
        <div className="error-popup">
          <div className="error-popup-content">
            <p>Error: Invalid Lobby Code</p>
            <button onClick={handleCloseError}>OK</button>
          </div>
        </div>
      )}
    </>
  );
}

export default PinPage;
