import React, { useState } from 'react';
import './PinPageStyle.css';

function PinPage() {
  const [lobbyCode, setLobbyCode] = useState('');
  const [showError, setShowError] = useState(false);

  const handleLobbyCodeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const numericInput = e.target.value.replace(/\D/g, '').slice(0, 4); // Limit to 4 digits
    setLobbyCode(numericInput);
  };

  const handleJoinGame = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          lobbyId: 1, // Use the actual lobby ID or pass it dynamically
          userId: 'playerUser', // Replace with the actual user ID or prompt for it
          pin: lobbyCode,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Lobby code is valid. Proceeding to join game...');
        setShowError(false); // Hide error if PIN is correct
        // Redirect to game or update the UI
      } else {
        console.error(data.error);
        setShowError(true); // Show error if PIN is incorrect
      }
    } catch (error) {
      console.error('Error joining the game:', error);
      setShowError(true);
    }
  };

  return (
    <div className="pin-page">
      <header className="header">
        <h1>Enter Lobby Code</h1>
        <input
          type="text"
          value={lobbyCode}
          onChange={handleLobbyCodeChange}
          placeholder="Enter 4-digit PIN"
        />
        <button onClick={handleJoinGame}>Join Game</button>
      </header>

      {showError && <div className="error">Invalid PIN. Please try again.</div>}
    </div>
  );
}

export default PinPage;
